visitScheduleRule:
"use strict";
({ params, imports }) => {
    const encounter = params.entity;
    const moment = imports.moment;
    const earliestDate = moment(encounter.encounterDateTime).toDate();
    const maxDate = moment(encounter.encounterDateTime).add(15, 'days').endOf('day').toDate();
    const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({encounter});

    function isAlreadyScheduled(encounterType, earliestDate) {
        const earliestDateTime = moment(earliestDate);
        const isAlreadyPresent = encounter.individual.getEncounters(true)
            .some((enc) => 
                enc &&
                enc.encounterType.name == encounterType &&
                enc.voided == false &&
                moment(enc.earliestVisitDateTime).isSame(earliestDateTime, 'day')
            );

        return isAlreadyPresent;
    }

    function scheduleVisit(encounterName, encounterType, earliestDate, maxDate) {
        if(!isAlreadyScheduled(encounterType, earliestDate)) {
            scheduleBuilder.add({
                name: encounterName, 
                encounterType: encounterType, 
                earliestDate, 
                maxDate
            });
        }
    }

    const referralMap = {
        'Subcenter Followup': ['22164309-c2d2-4a2b-9dee-5d9b5a205830', '64c1b238-e800-4581-8785-2d1897d0dd2e'],
        'VHW Followup': ['355143bf-b812-4ec8-93e6-6795fe4f7dc9'],
        'MCH Worker Followup': ['c1b3ea40-291f-49e7-893f-43565a477386'],
        'Cluster Coordinator Followup': [
            'e9a08867-baca-43ea-85ac-34c06811190b',
            'c21d00ba-91cd-404e-973d-f25246b15673'
        ]
    };

    const placeOfReferral = encounter.getObservationValue('c7b12d50-0a60-444a-a3cc-3dc13a2fb04e');
    if(placeOfReferral) {
        for (const [encounterName, uuids] of Object.entries(referralMap)) {
            if (uuids.includes(placeOfReferral)) {
                scheduleVisit(encounterName, encounterName, earliestDate, maxDate);
                break;
            }
        }
    }

    return scheduleBuilder.getAll();
};

