visitScheduleRule:
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const moment = imports.moment;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({programEncounter});

  const condition11 = new imports.rulesConfig.RuleCondition({programEncounter}).when.valueInEntireEnrolment("adac3441-95d8-47ef-ad3e-bcb7644f0627").defined.matches();

  if(condition11 ){
    const earliestDate = moment(programEncounter.programEnrolment.enrolmentDateTime).add(0, 'days').toDate();
    const maxDate = moment(programEncounter.programEnrolment.enrolmentDateTime).add(0, 'days').toDate();
    scheduleBuilder.add({name: "TB Study Risk Factors Assessment", encounterType: "TB Study Risk Factors Assessment", earliestDate, maxDate});  
}

  return scheduleBuilder.getAll();
};
