visitScheduleRule:
"use strict";
({ params, imports }) => {
  const encounter = params.entity;
  const moment = imports.moment;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({encounter});

  const condition11 = new imports.rulesConfig.RuleCondition({encounter}).when.valueInEncounter("871be6c0-2377-4ec6-add9-8b49d6dc9274").containsAnswerConceptName("57e20de7-10de-4391-b7ce-87b2f40d19a2").matches();

  if(condition11 ){
    const earliestDate = moment(encounter.encounterDateTime).add(0, 'days').toDate();
    const maxDate = moment(encounter.encounterDateTime).add(15, 'days').toDate();
    scheduleBuilder.add({name: "Referral Communication from VHW", encounterType: "Referral Communication from VHW", earliestDate, maxDate});  
}

  return scheduleBuilder.getAll();
};
